import { useEffect, useState } from 'react';
import logo from '../../peach.png';
import TodoForm from '../Todo/TodoForm.js'
import { CircleFill, TrashFill } from 'react-bootstrap-icons';

function Navbar() {
    const [todos, setTodos] = useState([])
    useEffect(() => {
        fetch('http://localhost:3001/get')
          .then(response => {
            if(!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          .then(data => setTodos(data))
          .catch(err => console.log(err));
    }, [])

    console.log(todos);


    //find todo item by id
    const handleEdit = (id) => {
      console.log('Current todos:', todos);
      const todo = todos.find((item) => item.id === id);
      if (!todo) {
        console.log('Todo item not found');
        return;
      }

      //Toggle the 'done' status
      const updatedTodo = {...todo, done: !todo.done };

      //send the updated todo item to the server
      fetch(`http://localhost:3001/update/${id}`, {
        method: 'PUT',
        headers: {
          'COntent-type': 'application/json',
        },
        body: JSON.stringify(updatedTodo),
      })
      .then(response => {
        if(!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(updatedItem => {
        // Update the state with the new todo list
        setTodos(todos.map((item) => item.id === id ? updatedItem : item));
      })
      .catch(err => console.log(err));
    }

  return (
    <nav className="Navbar">
      <img src={logo} className="App-logo" alt="logo" />
      <h1>What I gotta do.</h1>
      <TodoForm/>
      {
  todos.length === 0
    ? <div><h2>Nothing Planned Yet</h2></div>
    : todos.map(todo => ( // Assuming each todo has a unique id
        <div className='task' key={todo.id}> {/* Use todo.id instead of index for key */}
          <div className='checkbox' onClick={() => handleEdit(todo._id)}>
            <CircleFill className='icon' />
            <p>{todo.task}</p>
          </div>
          <div>
            <span /*onClick={() => handleDelete(todo.id)}*/><TrashFill className='icon'/></span>
          </div>
        </div>
      ))
}

    </nav>
  );
}

export default Navbar;
