import { useState } from "react";

function TodoForm() {
  const [task, setTask] = useState('')
  const handleAdd = async (e) => {
    e.preventDefault(); //Will prevent default form submission behavior
    try {
      const response = await fetch('http://localhost:3001/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({task: task}), //Send the task in the request body
      });

      if (response.ok) {
        const result = await response.json();
        console.log(result);
        setTask('') //clear the task after successful addition
      } else {
        throw new Error('Network response was not ok'); //Handle http errors
      }
    } catch (error) {
      console.log(error); //Handle network errors

    }
  }

  return (
    <form className='todo-form' onSubmit={handleAdd}>
      <input type='text'
        name='TodoItem'
        placeholder='... needs to be done'
        onChange={(e) => setTask(e.target.value)}
      />
      <button type='submit'>Add</button>
    </form>
  );
}

export default TodoForm;
