import Navbar from "../layout/Navbar";
import { useEffect, useState } from 'react';

function TodoForm() {
  const [todos, setTodos] = useState([])
    useEffect(() => {
        fetch('http://localhost:3001/get')
          .then(response => {
            if(!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          .then(data => {
            const todosWithId = data.map(todo => ({
              ...todo,
              id: todo._id.$oid
            }));
              setTodos(todosWithId);
          })
          .catch(err => console.log(err));
    }, [])

    console.log(todos);



  //Function for adding tasks
  const handleAdd = async (e) => {
    e.preventDefault(); //Will prevent default form submission behavior
    try {
      const response = await fetch('http://localhost:3001/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({task: task}), //Send the task in the request body
      });

      if (response.ok) {
        const result = await response.json();
        console.log(result);
        setTask('') //clear the task after successful addition
      } else {
        throw new Error('Network response was not ok'); //Handle http errors
      }
    } catch (error) {
      console.log(error); //Handle network errors

    }
  }

   //Handle edit function
   const handleEdit = (id) => {
    //log current todos to inspect them
    console.log('Current todos:', todos);
    //find todo item by id
    const todo = todos.find((item) => item.id === id);
    if (!todo) {
      console.log('Todo item not found');
      return;
    }

    //Toggle the 'done' status
    const updatedTodo = {...todo, done: !todo.done };

    //send the updated todo item to the server
    fetch(`http://localhost:3001/update/${id}`, {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify(updatedTodo),
    })
    .then(response => {
      if(!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(updatedItem => {
      // Update the state with the new todo list
      setTodos(todos.map((item) => item.id === id ? updatedItem : item));
    })
    .catch(err => console.log(err));
  }


  return (
    <form className='todo-form' onSubmit={handleAdd}>
      <input type='text'
        name='TodoItem'
        placeholder='... needs to be done'
        onChange={(e) => setTask(e.target.value)}
      />
      <button type='submit'>Add</button>
    </form>
  );
}

export default TodoForm;
